import React, { useState } from 'react';

const SettingsMenu = ({ onSettingsChange }) => {
  const [showTitle, setShowTitle] = useState(true);
  const [showContent, setShowContent] = useState(true);

  // Handle settings changes
  const handleShowTitleChange = (e) => {
    setShowTitle(e.target.checked);
    onSettingsChange({ showTitle: e.target.checked, showContent });
  };

  const handleShowContentChange = (e) => {
    setShowContent(e.target.checked);
    onSettingsChange({ showTitle, showContent: e.target.checked });
  };

  return (
    <div className="settings-menu">
      <h4>Settings</h4>
      <label>
        <input
          type="checkbox"
          checked={showTitle}
          onChange={handleShowTitleChange}
        />
        Show Title
      </label>
      <br />
      <label>
        <input
          type="checkbox"
          checked={showContent}
          onChange={handleShowContentChange}
        />
        Show Content
      </label>
    </div>
  );
};

export default SettingsMenu;
