import React from 'react';

const Widget = ({ widget, settings }) => {
  return (
    <div className="widget">
      {settings.showTitle && <h3>{widget.title}</h3>}  {/* Show title based on settings */}
      {settings.showContent && <p>{widget.content}</p>}  {/* Show content based on settings */}
    </div>
  );
};

export default Widget;
