import React from 'react';

const SettingsMenu = ({ addProgram, removeProgram, toggleDarkMode, changeAllDataTypes }) => {
  return (
    <div className="settings-menu">
      <h4>Settings</h4>
      <button onClick={addProgram}>Add Program</button>
      {/** Here we remove the program by its ID, but you can add a way to remove by selecting dynamically */}
      <button onClick={() => removeProgram(1)}>Remove Program 1</button>
      <br />
      <button onClick={() => changeAllDataTypes('graph')}>Show Graph for All</button>
      <button onClick={() => changeAllDataTypes('pie')}>Show Pie Chart for All</button>
      <button onClick={() => changeAllDataTypes('text')}>Show Text for All</button>
      <br />
      <label>
        <input
          type="checkbox"
          onChange={toggleDarkMode}
        />
        Dark Mode
      </label>
    </div>
  );
};

export default SettingsMenu;
