import React, { useState, useEffect } from 'react';
import Widget from './Widget'; // Assuming you have a Widget component to display the data

const Dashboard = () => {
  // Initial state for the widgets, with a "Fetching data..." default
  const [widgets, setWidgets] = useState([
    { id: 1, title: "Program 1", content: "Fetching data..." },
    { id: 2, title: "Program 2", content: "Fetching data..." },
    { id: 3, title: "Program 3", content: "Fetching data..." }
  ]);

  // Function to update a widget's content by its id
  const updateWidgetContent = (id, newContent) => {
    setWidgets(prevWidgets =>
      prevWidgets.map(widget => 
        widget.id === id ? { ...widget, content: newContent } : widget
      )
    );
  };

  // Fetch data for all programs when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data for Program 1
        const response1 = await fetch('http://localhost:3001/api/data/program1');
        if (!response1.ok) throw new Error('Failed to fetch Program 1');
        const result1 = await response1.json();
        updateWidgetContent(1, result1.content);

        // Fetch data for Program 2
        const response2 = await fetch('http://localhost:3001/api/data/program2');
        if (!response2.ok) throw new Error('Failed to fetch Program 2');
        const result2 = await response2.json();
        updateWidgetContent(2, result2.content);

        // Fetch data for Program 3
        const response3 = await fetch('http://localhost:3001/api/data/program3');
        if (!response3.ok) throw new Error('Failed to fetch Program 3');
        const result3 = await response3.json();
        updateWidgetContent(3, result3.content);

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Call the fetchData function to load the data
    fetchData();
  }, []); // The empty dependency array ensures this runs once on component mount

  return (
    <div className="dashboard">
      {widgets.map(widget => (
        <Widget key={widget.id} widget={widget} />
      ))}
    </div>
  );
};

export default Dashboard;
