import React, { useState } from 'react';
import Widget from './Widget';
import SettingsMenu from './SettingsMenu';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Dashboard = () => {
  const [widgets, setWidgets] = useState([
    { id: 1, title: "Program 1", content: "Fetching data...", type: "text" },
    { id: 2, title: "Program 2", content: "Fetching data...", type: "text" }
  ]);
  const [nextProgramId, setNextProgramId] = useState(3); // Tracks the next program number
  const [settings, setSettings] = useState({
    darkMode: false,
  });

  // Add a new program with a unique ID
  const addProgram = () => {
    const newProgram = {
      id: nextProgramId,
      title: `Program ${nextProgramId}`,
      content: "Fetching data...",
      type: "text"
    };
    setWidgets([...widgets, newProgram]);
    setNextProgramId(nextProgramId + 1); // Increment the next program ID
  };

  // Remove a program by id
  const removeProgram = (id) => {
    setWidgets(widgets.filter(widget => widget.id !== id));
  };

  // Toggle dark/light mode
  const toggleDarkMode = () => {
    setSettings(prevSettings => ({
      ...prevSettings,
      darkMode: !prevSettings.darkMode,
    }));
  };

  // Handle drag and drop
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const reorderedWidgets = Array.from(widgets);
    const [removed] = reorderedWidgets.splice(source.index, 1);
    reorderedWidgets.splice(destination.index, 0, removed);

    setWidgets(reorderedWidgets);
  };

  // Change the data type of a program
  const changeDataType = (id, type) => {
    setWidgets(
      widgets.map(widget => widget.id === id ? { ...widget, type } : widget)
    );
  };

  // Change the data type for all programs
  const changeAllDataTypes = (type) => {
    setWidgets(
      widgets.map(widget => ({ ...widget, type }))
    );
  };

  return (
    <div className={`dashboard ${settings.darkMode ? 'dark-mode' : ''}`}>
      <SettingsMenu 
        addProgram={addProgram} 
        removeProgram={removeProgram} 
        toggleDarkMode={toggleDarkMode}
        changeAllDataTypes={changeAllDataTypes}
      />
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="dashboard">
          {(provided) => (
            <div className="dashboard" {...provided.droppableProps} ref={provided.innerRef}>
              {widgets.map((widget, index) => (
                <Draggable key={widget.id} draggableId={`${widget.id}`} index={index}>
                  {(provided) => (
                    <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                      <Widget widget={widget} />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default Dashboard;
