import React, { useState, useEffect } from 'react';
import Widget from './Widget';

const Dashboard = () => {
  const [widgets, setWidgets] = useState([
    { id: 1, title: "Program 1", content: "Fetching data..." },
    { id: 2, title: "Program 2", content: "Fetching data..." },
    { id: 3, title: "Program 3", content: "Fetching data..." }
  ]);

  // Function to update widget content
  const updateWidgetContent = (id, newContent) => {
    setWidgets(widgets.map(widget => widget.id === id ? { ...widget, content: newContent } : widget));
  };

  // Fetch data for widgets
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response1 = await fetch(`http://localhost:3001/api/data/program1`);
        const result1 = await response1.json();
        updateWidgetContent(1, result1.content);

        const response2 = await fetch(`http://localhost:3001/api/data/program2`);
        const result2 = await response2.json();
        updateWidgetContent(2, result2.content);

        const response3 = await fetch(`http://localhost:3001/api/data/program3`);
        const result3 = await response3.json();
        updateWidgetContent(3, result3.content);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array to run on mount only

  return (
    <div className="dashboard">
      {widgets.map(widget => (
        <Widget 
          key={widget.id} 
          widget={widget} 
          updateWidgetContent={updateWidgetContent} 
        />
      ))}
    </div>
  );
};

export default Dashboard;
