import React from 'react';

const SettingsMenu = ({ addProgram, removeProgram, toggleDarkMode, changeDataType }) => {
  return (
    <div className="settings-menu">
      <h4>Settings</h4>
      <button onClick={addProgram}>Add Program</button>
      <button onClick={() => removeProgram(1)}>Remove Program 1</button>
      <br />
      <label>
        <input
          type="checkbox"
          onChange={toggleDarkMode}
        />
        Dark Mode
      </label>
      <br />
      <button onClick={() => changeDataType(1, 'graph')}>Show Graph for Program 1</button>
      <button onClick={() => changeDataType(1, 'pie')}>Show Pie Chart for Program 1</button>
      <button onClick={() => changeDataType(1, 'text')}>Show Text for Program 1</button>
    </div>
  );
};

export default SettingsMenu;
